# variables definition
@variable(model, x[1:88]; binary=true)  # principle place in county i
@variable(model, y[1:88]; binary=true)  # county i covered or not

# constraints
@constraint(model, A*x .>= y)
@constraint(model, sum(x[1:88]) <= k)

# glide path restrictions
# candidate [3, 5, 7, 8, 9, 28, 35, 40, 45, 49, 51, 69, 72, 75, 76]
@constraint(model, x[1:2]  .== 0)
@constraint(model, x[4]  .== 0)
@constraint(model, x[6]  .== 0)
@constraint(model, x[10:27]  .== 0)
@constraint(model, x[29:34]  .== 0)
@constraint(model, x[36:39]  .== 0)
@constraint(model, x[41:44]  .== 0)
@constraint(model, x[46:48]  .== 0)
@constraint(model, x[50]  .== 0)
@constraint(model, x[52:68]  .== 0)
@constraint(model, x[70:71]  .== 0)
@constraint(model, x[73:74]  .== 0)
@constraint(model, x[77:88]  .== 0)

# constraints for k = 10
@constraint(model, x[9] .== 1)  # Butler
@constraint(model, x[28] .== 1)  # Geauga
@constraint(model, x[35] .== 1)  # Henry
@constraint(model, x[45] .== 1)  # Licking
@constraint(model, x[76] .== 1)  # Stark

# the objective
@objective(model, Max,
    sum(
    population[i]*y[i]
    for i in 1:88)
)

print(model)

# call optimizer
optimize!(model)

# show results
@show objective_value(model)
@show value.(x)
@show value.(y)



